create table EMAIL_RECIPIENTS (EMAIL_RECIPIENTS_TYPE varchar2(31 char) not null, EMAIL_RECIPIENTS_ID number(19,0) not null, EMAIL_RECIPIENTS_ADDRESS_ID number(19,0) not null, EMAIL_RECIPIENTS_EMAIL_ID number(19,0) not null, primary key (EMAIL_RECIPIENTS_ID));
alter table EMAIL_RECIPIENTS add constraint FK_EMAIL_RECIPIENTS_EMAIL_ID foreign key (EMAIL_RECIPIENTS_EMAIL_ID) references EMAIL(EMAIL_ID);
alter table EMAIL_RECIPIENTS add constraint FK_EMAIL_RECIPIENTS_ADDRESS_ID foreign key (EMAIL_RECIPIENTS_ADDRESS_ID) references EMAIL_ADDRESS(EMAIL_ADDRESS_ID);
create sequence EMAIL_RECIPIENTS_SEQUENCE;